@use '../base';
body {
  font-family: "Space Mono", monospace;
}
.wrapper {
  background-color: base.$main-bg;
}
.main {
  background-color: base.$white;
  margin-top: 2.5rem;
  border-radius: 25px 25px 0 0;
}
.form {
  padding: 2rem;
  max-width: 520px;
  margin: auto;
}
.lbl {
  font-size: base.calculateRem(24);
  font-weight: 700;
  display: block;
  width: 100%;
  height: 100%;
  //  left: 0;
  background-color: base.$button-green;
  color: base.$white;
  text-align: center;
  border-radius: 5px;
  padding: 0.35rem;
  z-index: 2;
  position: relative;
}
.customlbl {
  z-index: 1;
}
.custom {
  width: 100%;
  height: 100%;
  border-radius: 5px;
  border: none;
  text-align: center;
  background-color: base.$lt-grey;
  color: #547878;
  font-size: base.calculateRem(24);
  font-weight: 700;
}
.percent {
  position: absolute;
  top: 0;
  z-index: 1;
}
.select__wrapper {
  display: flex;
  flex-direction: row;
  gap: 1rem;
  flex-wrap: wrap;
  padding-bottom: 2rem;
}
.input__wrapper {
  flex: 1 1 45%;
}
.bill__total {
  position: relative;
}
.bill__label {
  display: block;
  font-weight: 700;
  font-size: base.calculateRem(16);
  color: base.$mid-grey;

  &::before {
    position: absolute;
    content: "";
    width: 40px;
    height: 40px;
    background-image: url("../../images/icon-dollar.svg");
    background-repeat: no-repeat;
    bottom: 0rem;
    z-index: 1;
    left: 1rem;
  }
}
.bill {
  position: relative;
  background-color: base.$lt-grey;
  border: none;
  text-align: right;
  width: 100%;
  padding: 0.5rem;
  font-size: base.calculateRem(24);
  font-weight: 700;
  border-radius: 5px;
}
.select {
  margin-top: 2rem;
  border: none;
}

.input__wrapper {
  position: relative;
}

.labels {
  font-size: base.calculateRem(16);
  font-weight: 700;
  color: base.$mid-grey;
}

.num__people {
  background-color: base.$lt-grey;
  color: base.$dark-green;
  font-size: base.calculateRem(24);
  font-weight: 700;
  border: none;
  width: 100%;
  padding: 0.5rem;
  border-radius: 5px;
  text-align: right;
}

.lbl__people {
  position: relative;
  &::before {
    position: absolute;
    display: inline-block;
    top: 2.5rem;
    content: "";
    width: 40px;
    height: 40px;
    background-image: url("../../images/icon-person.svg");
    background-repeat: no-repeat;
    bottom: 0rem;
    z-index: 1;
    left: 1rem;
  }
}

.tip, .total {
  display: flex;
  flex-direction: column;
}
.tip__person, .total__person {
	display: flex;
	flex-direction: row;
	justify-content: space-between;
	align-items: center;
}

